mapper.select:
select a.ID, a.PRODUCTNAME, a.PRODUCTREFERRED, a.RETURNSBENCHMARK, a.RETURNSBENCHMARKMAX, a.INVESTMENTSTARTING, a.PRODUCTDEADLINE, a.PRODUCTREVIEWS, a.THEISSUER, b.NAME THEISSUERNAME, b.IMGURL THEISSUERIMGURL, a.EXPECTISSUANCE, a.INTERESTDISTRIBUTIONSTATE, c.NAME TINTERESTDISTRIBUTIONSTATENAME, a.TRUSTTYPE, d.NAME TRUSTTYPENAME, a.PRODUCTSTATUS, e.NAME PRODUCTSTATUSNAME, a.INVESTMENTSTARTINGSTATE, a.PRODUCTDEADLINESTATE, a.RETURNSBENCHMARKSTATE, a.DETAILS, a.SORT, a.CTDATE, a.UPDATE, a.TYPE from td_product a left join td_institution b on a.THEISSUER = b.ID left join td_conditiontwo c on a.INTERESTDISTRIBUTIONSTATE = c.ID left join td_conditiontwo d on a.TRUSTTYPE = d.ID left join td_conditiontwo e on a.PRODUCTSTATUS = e.ID where 1 = 1
AND a.ID != #{ID}
AND a.TYPE != #{TYPE}
ORDER BY a.SORT LIMIT 0,4

select a.ID, a.PRODUCTNAME, a.PRODUCTREFERRED, a.RETURNSBENCHMARK, a.RETURNSBENCHMARKMAX, a.INVESTMENTSTARTING, a.PRODUCTDEADLINE, a.PRODUCTREVIEWS, a.THEISSUER, e.NAME THEISSUERNAME, a.EXPECTISSUANCE, a.INTERESTDISTRIBUTIONSTATE, b.NAME TINTERESTDISTRIBUTIONSTATENAME, a.TRUSTTYPE, c.NAME TRUSTTYPENAME, a.PRODUCTSTATUS, d.NAME PRODUCTSTATUSNAME, a.INVESTMENTSTARTINGSTATE, a.PRODUCTDEADLINESTATE, a.RETURNSBENCHMARKSTATE, (select group_concat(n.NAME) from td_product m left JOIN td_conditiontwo n on FIND_IN_SET(n.ID,m.INVESTMENTSTARTINGSTATE) where m.ID = a.ID) INVESTMENTSTARTINGNAMES, (select group_concat(n.NAME) from td_product m left JOIN td_conditiontwo n on FIND_IN_SET(n.ID,m.PRODUCTDEADLINESTATE) where m.ID = a.ID) PRODUCTDEADLINENAMES, (select group_concat(n.NAME) from td_product m left JOIN td_conditiontwo n on FIND_IN_SET(n.ID,m.RETURNSBENCHMARKSTATE) where m.ID = a.ID) RETURNSBENCHMARKNAMES, a.DETAILS, a.SORT, a.CTDATE, a.UPDATE, a.TYPE, (case when a.TYPE = '1' then "信托" when a.TYPE = '2' then "资管" when a.TYPE = '3' then "契约型" when a.TYPE = '4' then "股权投资" when a.TYPE = '5' then "政府债" when a.TYPE = '6' then "对冲•定增" end) TYPENAME from td_product a left join td_conditiontwo b on a.INTERESTDISTRIBUTIONSTATE = b.ID left join td_conditiontwo c on a.TRUSTTYPE = c.ID left join td_conditiontwo d on a.PRODUCTSTATUS = d.ID left join td_institution e on a.THEISSUER = e.ID where 1 = 1
AND a.INTERESTDISTRIBUTIONSTATE = #{pd.INTERESTDISTRIBUTIONSTATE}
AND a.TRUSTTYPE = #{pd.TRUSTTYPE}
AND a.TYPE = #{pd.TYPE}
AND a.PRODUCTSTATUS = #{pd.PRODUCTSTATUS}
find_in_set("${item}",a.INVESTMENTSTARTINGSTATE)
find_in_set("${item}",a.PRODUCTDEADLINESTATE)
find_in_set("${item}",a.RETURNSBENCHMARKSTATE)
ORDER BY a.CTDATE DESC

select a.ID, a.PRODUCTNAME, a.PRODUCTREFERRED, a.RETURNSBENCHMARK, a.RETURNSBENCHMARKMAX, a.INVESTMENTSTARTING, a.PRODUCTDEADLINE, a.PRODUCTREVIEWS, a.THEISSUER, b.NAME THEISSUERNAME, b.IMGURL THEISSUERIMGURL, a.EXPECTISSUANCE, a.INTERESTDISTRIBUTIONSTATE, c.NAME TINTERESTDISTRIBUTIONSTATENAME, a.TRUSTTYPE, d.NAME TRUSTTYPENAME, a.PRODUCTSTATUS, e.NAME PRODUCTSTATUSNAME, a.INVESTMENTSTARTINGSTATE, a.PRODUCTDEADLINESTATE, a.RETURNSBENCHMARKSTATE, a.DETAILS, a.SORT, a.CTDATE, a.UPDATE, a.TYPE from td_product a left join td_institution b on a.THEISSUER = b.ID left join td_conditiontwo c on a.INTERESTDISTRIBUTIONSTATE = c.ID left join td_conditiontwo d on a.TRUSTTYPE = d.ID left join td_conditiontwo e on a.PRODUCTSTATUS = e.ID where 1 = 1
AND a.TYPE = #{pd.TYPE}
AND a.PRODUCTREFERRED LIKE CONCAT(CONCAT('%', #{pd.SEARCHNAME}),'%')
AND (a.PRODUCTNAME LIKE CONCAT(CONCAT('%', #{pd.SEARCHINGNAME}),'%') OR a.PRODUCTREFERRED LIKE CONCAT(CONCAT('%', #{pd.SEARCHINGNAME}),'%') OR b.NAME LIKE CONCAT(CONCAT('%', #{pd.SEARCHINGNAME}),'%'))
AND a.INTERESTDISTRIBUTIONSTATE = #{pd.INTERESTDISTRIBUTIONSTATE}
AND a.TRUSTTYPE = #{pd.TRUSTTYPE}
AND a.PRODUCTSTATUS = #{pd.PRODUCTSTATUS}
AND a.INVESTMENTSTARTINGSTATE LIKE CONCAT(CONCAT('%', #{pd.INVESTMENTSTARTINGSTATE}),'%')
AND a.PRODUCTDEADLINESTATE LIKE CONCAT(CONCAT('%', #{pd.PRODUCTDEADLINESTATE}),'%')
AND a.RETURNSBENCHMARKSTATE LIKE CONCAT(CONCAT('%', #{pd.RETURNSBENCHMARKSTATE}),'%')
ORDER BY a.SORT,a.CTDATE DESC
ORDER BY a.CTDATE DESC,a.SORT

SELECT a.ID, b.NAME THEISSUERNAME, b.IMGURL THEISSUERIMGURL, a.PRODUCTREVIEWS, a.PRODUCTREFERRED, a.PRODUCTDEADLINE, a.EXPECTISSUANCE, a.RETURNSBENCHMARK, a.RETURNSBENCHMARKMAX, a.INVESTMENTSTARTING, "1" PRODUCTTYPE, c.NAME TRUSTTYPENAME FROM td_product a left join td_institution b on a.THEISSUER = b.ID left join td_conditiontwo c on a.TRUSTTYPE = c.ID WHERE 1=1
AND a.TYPE = #{TYPE}
order by IF(a.sort is null ,1,0),a.SORT ASC ,a.CTDATE DESC LIMIT 0,5
