mapper.select:
select a.ID, a.NAME, a.PHONE, a.COMMENTS, a.FINANCIALPLANNERID, a.CTDATE, a.UPDATE, a.STATUS from td_financialplannerappointment a where 1=1
and a.ID = #{ID}
and a.NAME = #{NAME}

select a.ID, a.NAME, a.PHONE, a.COMMENTS, a.FINANCIALPLANNERID, a.CTDATE, a.UPDATE, a.STATUS, b.NAME FINANCIALPLANNERNAME from td_financialplannerappointment a left join td_financialplanner b on a.FINANCIALPLANNERID = b.ID where 1=1
and a.STATUS = #{pd.STATUS}
order by a.CTDATE DESC
